import React from "react";

function ComposeBox({ setActiveSection, composeData, setComposeData, setGeneratedMessage }) {
    const handleInputChange = (e) => {
        setComposeData({
            ...composeData,
            [e.target.name]: e.target.value,
        });
    };

    const handleTemplateSelection = (template) => {
        setComposeData({
            ...composeData,
            keywords: template.keywords,
            theme: template.theme,
            tone: template.tone,
            relationship: template.relationship,
            language: template.language,
        });
    };

    const handleCompose = async () => {
        const message = `Generated AI message for ${composeData.recipient} with theme: ${composeData.theme}.`;
        setGeneratedMessage(message);
        setActiveSection("message");
    };

    return (
        <div className="card compose-box">
            <div className="input-group">
                <div className="input-row">
                    <label>Recipient (To)</label>
                    <input type="text" name="recipient" placeholder="John Doe" value={composeData.recipient || ""} onChange={handleInputChange} />
                    <label>Sender (From)</label>
                    <input type="text" name="sender" placeholder="Jane Smith" value={composeData.sender || ""} onChange={handleInputChange} />
                </div>

                <div className="input-row">
                    <label>Keywords</label>
                    <input type="text" name="keywords" placeholder="birthday, celebration, happy" value={composeData.keywords || ""} onChange={handleInputChange} />
                    <label>Word Count</label>
                    <input type="number" name="wordCount" placeholder="100" value={composeData.wordCount || 100} onChange={handleInputChange} />
                </div>

                <div className="input-row">
                    <label>Theme</label>
                    <input type="text" name="theme" placeholder="Birthday" value={composeData.theme || ""} onChange={handleInputChange} />
                    <label>Tone</label>
                    <input type="text" name="tone" placeholder="Casual" value={composeData.tone || ""} onChange={handleInputChange} />
                </div>

                <div className="input-row">
                    <label>Relationship</label>
                    <input type="text" name="relationship" placeholder="Friend" value={composeData.relationship || ""} onChange={handleInputChange} />
                    <label>Language</label>
                    <input type="text" name="language" placeholder="English" value={composeData.language || ""} onChange={handleInputChange} />
                </div>

                <button onClick={handleCompose}>üìù Compose</button>

                {/* Pre-Built Templates */}
                <div className="templates">
                    <h4>Pre-Built Message Templates</h4>
                    <button onClick={() => handleTemplateSelection({ keywords: "Thank You, Appreciation", theme: "Gratitude", tone: "Formal", relationship: "Colleague", language: "English" })}>
                        üôè Thank You Message
                    </button>
                    <button onClick={() => handleTemplateSelection({ keywords: "Love, Forever, Special", theme: "Romantic", tone: "Sweet", relationship: "Partner", language: "English" })}>
                        ‚ù§Ô∏è Love Message
                    </button>
                    <button onClick={() => handleTemplateSelection({ keywords: "Party, Celebration, Happy", theme: "Birthday", tone: "Excited", relationship: "Friend", language: "English" })}>
                        üéÇ Birthday Message
                    </button>
                    <button onClick={() => handleTemplateSelection({ keywords: "Apology, Regret", theme: "Apology", tone: "Sincere", relationship: "Friend", language: "English" })}>
                        üôá Apology Message
                    </button>
                    <button onClick={() => handleTemplateSelection({ keywords: "Congratulations, Achievement", theme: "Congratulatory", tone: "Motivational", relationship: "Colleague", language: "English" })}>
                        üéâ Congratulations Message
                    </button>
                </div>
            </div>
        </div>
    );
}

export default ComposeBox;
