import React, { useEffect, useState } from "react";
import { FaMoon, FaSun, FaBell } from "react-icons/fa";
import { db, collection, query, where, onSnapshot } from "../firebase"; // Firebase setup

function TitleBar() {
    const [darkMode, setDarkMode] = useState(false);
    const [unreadMessages, setUnreadMessages] = useState(false);

    useEffect(() => {
        // Firebase listener for real-time message updates
        const messagesQuery = query(collection(db, "messages"), where("status", "==", "unread"));
        const unsubscribe = onSnapshot(messagesQuery, (snapshot) => {
            setUnreadMessages(snapshot.docs.length > 0);
        });

        return () => unsubscribe();
    }, []);

    const toggleDarkMode = () => {
        setDarkMode(!darkMode);
        document.body.classList.toggle("dark-mode", !darkMode);
    };

    return (
        <header className="title-bar">
            <h1>âœ¨ AutoMessageApp ðŸš€</h1>
            <div className="title-icons">
                <button className="notification-icon">
                    <FaBell />
                    {unreadMessages && <span className="notification-badge"></span>}
                </button>
                <button className="dark-mode-toggle" onClick={toggleDarkMode}>
                    {darkMode ? <FaSun /> : <FaMoon />}
                </button>
            </div>
        </header>
    );
}

export default TitleBar;
