import React, { useState } from "react";

function MessageBox({ generatedMessage }) {
    const [editableMessage, setEditableMessage] = useState(generatedMessage);

    const handleEdit = () => {
        const newMessage = prompt("Edit Message:", editableMessage);
        if (newMessage) setEditableMessage(newMessage);
    };

    const handleCopy = () => {
        navigator.clipboard.writeText(editableMessage);
        alert("Message copied!");
    };

    return (
        <div className="card message-box">
            <p>{editableMessage || "No message generated yet."}</p>
            <div className="action-buttons">
                <button onClick={handleCopy}>üìã Copy</button>
