import React, { useState } from "react";

function MessageBox() {
    const [message, setMessage] = useState("Generated message will appear here.");

    const handleEdit = () => {
        const editedMessage = prompt("Edit Message:", message);
        if (editedMessage) setMessage(editedMessage);
    };

    const handleCopy = () => {
        navigator.clipboard.writeText(message);
        alert("Message copied!");
    };

    return (
        <div className="card message-box">
            <p>{message}</p>
            <div className="action-buttons">
                <button onClick={handleCopy}>üìã Copy</button>
                <button onClick={handleEdit}>‚úèÔ∏è Edit</button>
                <button>üíæ Save</button>
                <button>üì§ Share</button>
                <button>üì© Send</button>
                <button>üìß Email</button>
                <button>‚≠ê Favorite</button>
                <button>üîÑ Refresh</button>
            </div>
        </div>
    );
}

export default MessageBox;
