import React, { useContext, useState } from "react";
import { ProfileContext } from "../context/ProfileContext";

export default function Compose() {
    const profileContext = useContext(ProfileContext);
    if (!profileContext) {
        return <div>Error: Profile context is not available</div>;
    }

    const { profiles } = profileContext;
    const [selectedProfile, setSelectedProfile] = useState(profiles[0] || "Personal");
    const [message, setMessage] = useState("");

    const generateMessage = () => {
        setMessage(`AI-generated message based on ${selectedProfile} profile.`);
    };

    return (
        <div className="compose-page">
            <h2>AI Messaging</h2>
            <div className="profile-selector">
                <label>Select Profile:</label>
                <select value={selectedProfile} onChange={(e) => setSelectedProfile(e.target.value)}>
                    {profiles.map((profile, index) => (
                        <option key={index} value={profile}>
                            {profile}
                        </option>
                    ))}
                </select>
            </div>
            <div className="message-box">
                <textarea value={message} readOnly placeholder="AI-generated message will appear here..." />
            </div>
            <button onClick={generateMessage}>âœ¨ Generate AI Message</button>
        </div>
    );
}
